<template>
  <div class="question">
    <h6>
      <div class="img" @click="collection">
        <img v-show="!collected" src="data:image/png;base64," alt="" width="100%">

        <img v-show="collected" src="data:image/png;base64," alt="" width="100%">
      </div>
    </h6>
    <h1><em>{{type ==1 ?(order+1) : order}}、</em>{{title}}</h1>
    <p v-for="(item, index) in answer" :key="item" @click="checkAnswer(item)" :class="{'right': (index+1) == rightAnsInd}">{{item}}</p>
    <mt-button type="primary" size="normal" @click="matchQuestion">下一题</mt-button>
  </div>
</template>

<script>
export default {
  name: 'question',
  data () {
    return {
      type: 1,    // 答题模式
      questions: [],
      exitedQuestion: [],
      order: -1,
      title: '',
      answer: [],
      rightAns: '',
      rightAnsInd: 0,    // 正确选项的序列号
      collected: false,     // 已收藏
      next: false     // 可以点击下一题了
    }
  },
  mounted () {
    this.type = this.$route.params.type;    // 从url中获取答题模式
    this.$axios.get('/static/question.json').then(res => {    // 获取全部题库
      this.questions = res.data.records;
      this.matchQuestion();
    }, res => {
        // error callback
    })
  },
  methods: {
    matchQuestion () {    // 整理全部题库
      this.rightAnsInd = 0;     // 去除选项绿色高亮
      this.getNextQuestionOrder(this.questions);
      console.log('id: ', this.order);
      this.title = this.questions[this.order]['title'];
      this.rightAns = this.questions[this.order]['rightAns'];
      let ansArr = this.questions[this.order]['answer'];
      this.answer = [];
      for(let i in ansArr) {
        this.answer.push(ansArr[i]);
      }
      console.log('打乱前： ', this.answer);
      this.randomsort(this.answer);
      console.log('打乱后： ', this.answer);
      this.next = true;   // 现在可以点击下一题了
    },
    getNextQuestionOrder (_qes) {
      if(this.type == 1) {    // 顺序模式
        this.order++;
      }
      if(this.type == 2) {    // 随机模式，随机获取题目ID
        let randomTit = parseInt(_qes.length * Math.random());
        for(let i in this.exitedQuestion) {     // 判断之前答过的题目中时候有本次随机到的题目
          if(randomTit == this.exitedQuestion[i]) {
            this.getNextQuestionOrder();
            return;
          }
        }
        this.exitedQuestion.push(_qes[randomTit].id);
        this.order = randomTit;
      }
    },
    checkAnswer(ans) {    // 验证答案是否正确
      if(this.next) {
        for(let i = 0; i < this.answer.length; i++) {
          if(this.answer[i] == this.rightAns) {
            this.rightAnsInd = i+1;
          }
        }
        event.target.className = 'error';
      }
      this.next = false;
    },
    randomsort(arr) {    // 随机打乱数组顺序
      for (let i = 1; i < arr.length; i++) {
        const random = Math.floor(Math.random() * (i + 1));
        [arr[i], arr[random]] = [arr[random], arr[i]];
      }
    },
    collection () {      // 收藏
      this.collected = !this.collected;
    }
  }
}
</script>

